package Tutorial1;

public class CreatingMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Kevin came across Emre in Downtown");		
		sayHi();
		System.out.println("Kevin said he was busy and needed to go");
	}

	public static void sayHi() {
		
		System.out.println("Hello Emre");
		//so we created sayHi method down here
		//inside the main method we can just call as sayHi();
		//it will bring it in main method and execute it
		
	}
	
	
	
	
}
