package Tutorial1;

public class MathWithMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=3;
		double b=-5.5;
		double c =36.0;
		double d = 3.7;
		double e = 3.3;
	
				
		System.out.println(Math.abs(b));
		// it will give absolute value so instead of -5.5 it'll give 5.5
		
		System.out.println(Math.pow(a, 2));
		// Squared a 3^2=9  , second power of a
		
		System.out.println(Math.sqrt(c));
		//square root of c 
		
		System.out.println(Math.min(a, b));
		//it will give the smaller number between a and b
		
		System.out.println(Math.max(a, b));
		//it will give the bigger number between a and b
		
		System.out.println(Math.round(d));
		//it will round the d value 3.7 to 4
		
		System.out.println(Math.round(e));
		//it will round the d value 3.3  to 3
		

		
		
		

	}

}
