package Tutorial1;

public class Math_and_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(3+4); //SUM
		System.out.println(3-4); //Substraction
		System.out.println(3*4);//Multiply
		
		System.out.println(3/4); // Division
		
		// Only problem using Division with if you enter to integers
		// Result will be giving in integer
		//if you want exact result enter at least one number in double data type
		
		System.out.println(3/4.0); // Result will be double
		
		System.out.println(10 % 3); // it will give whatever left as remainder
		
		//3*3 = 9 so remainder is 1 from 10. it'll print 1
		
		//you can also use variables then do the math
		
		int a=3;
		double b=4.0;
		
		System.out.println(a/b);
		
		
		
		
		
	}

}
